svg.script:

(function() {
  const svg = document.querySelector('svg');
  const cursorGlow = document.getElementById('cursor-glow');
  const particles = document.querySelectorAll('.particle');

  // Mouse movement tracking
  svg.addEventListener('mousemove', function(e) {
    const rect = svg.getBoundingClientRect();
    const x = (e.clientX - rect.left) * (1600 / rect.width);
    const y = (e.clientY - rect.top) * (800 / rect.height);

    cursorGlow.setAttribute('cx', x);
    cursorGlow.setAttribute('cy', y);
    cursorGlow.style.opacity = '0.3';

    // Make particles react to mouse
    particles.forEach(particle => {
      const px = parseFloat(particle.getAttribute('cx'));
      const py = parseFloat(particle.getAttribute('cy'));
      const distance = Math.sqrt(Math.pow(x - px, 2) + Math.pow(y - py, 2));

      if (distance < 200) {
        particle.style.opacity = '1';
        const scale = 1 + (200 - distance) / 200;
        particle.setAttribute('r', parseFloat(particle.getAttribute('r')) * scale);
      }
    });
  });

  svg.addEventListener('mouseleave', function() {
    cursorGlow.style.opacity = '0.1';
  });

  // Interactive elements pulse on hover
  const interactiveElements = document.querySelectorAll('.interactive');
  interactiveElements.forEach(el => {
    el.addEventListener('mouseenter', function() {
      this.style.transform = 'scale(1.05)';
    });
    el.addEventListener('mouseleave', function() {
      this.style.transform = 'scale(1)';
    });
  });

  // Click on hexagon to create ripple
  const hexGroup = document.querySelector('.hexagon-group');
  hexGroup.addEventListener('click', function(e) {
    const ripple = document.createElementNS('http://www.w3.org/2000/svg', 'circle');
    ripple.setAttribute('cx', '0');
    ripple.setAttribute('cy', '0');
    ripple.setAttribute('r', '10');
    ripple.setAttribute('fill', 'none');
    ripple.setAttribute('stroke', '#00ff88');
    ripple.setAttribute('stroke-width', '3');

    const animate1 = document.createElementNS('http://www.w3.org/2000/svg', 'animate');
    animate1.setAttribute('attributeName', 'r');
    animate1.setAttribute('from', '10');
    animate1.setAttribute('to', '100');
    animate1.setAttribute('dur', '1s');
    animate1.setAttribute('fill', 'remove');

    const animate2 = document.createElementNS('http://www.w3.org/2000/svg', 'animate');
    animate2.setAttribute('attributeName', 'opacity');
    animate2.setAttribute('from', '1');
    animate2.setAttribute('to', '0');
    animate2.setAttribute('dur', '1s');
    animate2.setAttribute('fill', 'remove');

    ripple.appendChild(animate1);
    ripple.appendChild(animate2);
    hexGroup.appendChild(ripple);

    setTimeout(() => ripple.remove(), 1000);
  });
})();
